import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ShowAndBooks() {
  const [concertData, setConcertData] = useState([]);

  const [originalData, setOriginalData] = useState([]);
  const  [review,setReview]=useState(false)
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const [star,setStar]=useState()
const [collapse,setCollapse]=useState(true)
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndBookId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData(data);
          setOriginalData(data)
        } else {
          setConcertData([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setConcertData([]);
      }
    };
    fetchData();
  }, []);
  const [concertData2, setConcertData2] = useState([]);
  const [searchedId, setSearchedId] = useState('');
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = concertData.slice(indexOfFirstItem, indexOfLastItem);
  const currentItems2 = concertData2.slice(indexOfFirstItem, indexOfLastItem);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const navigate = useNavigate();
  // const [values, setValues] = useState({
  //   cname: '',
  //   cimage: '',
  //   tname: '',
  //   timage: '',
  //   date: '',
  //   time: '',
  //   ticket: '',
  // });
    // const [RegisterStatus, setRegisterStatus] = useState("");
    // const navigate = useNavigate();
    // const [values, setValues] = useState({
    //   cname: '',
    //   cimage: '',
    //   tname: '',
    //   timage: '',
    //   date: '',
    //   time: '',
    //   ticket: '',
    // });
    // const [errors, setErrors] = useState({});
    // const [concertData, setConcertData] = useState([]); // New state to store the retrieved data
  
    const ReviewSubmit = (e) => {
      e.preventDefault();
      console.log(comment)
      fetch("http://localhost:8081/Review", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          review: comment,
          rating:star ,
          book_id:searchedId,
          // ticket: values.ticket,
          // password: values.password,
        //   data: values.date,
        //   time: values.time,
        //   ticket: values.ticket
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.message) {
            // setRegisterStatus(data.message);
            setReview(false)
            navigate('/ShowAndBooks');
          } else {
            // setRegisterStatus("Concert created");
            console.log(data);
            setReview(false)

            navigate('/ShowAndBooks');
