import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ShowAndBooks() {
  const [concertData, setConcertData] = useState([]);
  const  [review,setReview]=useState(false)
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
const [collapse,setCollapse]=useState(true)
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndBookId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setConcertData(data);
        } else {
          setConcertData([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setConcertData([]);
      }
    };
    fetchData();
  }, []);
  const [concertData2, setConcertData2] = useState([]);
  const [searchedId, setSearchedId] = useState('');
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = concertData.slice(indexOfFirstItem, indexOfLastItem);
  const currentItems2 = concertData2.slice(indexOfFirstItem, indexOfLastItem);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const navigate = useNavigate();
  // const [values, setValues] = useState({
  //   cname: '',
  //   cimage: '',
  //   tname: '',
  //   timage: '',
  //   date: '',
  //   time: '',
  //   ticket: '',
  // });
    // const [RegisterStatus, setRegisterStatus] = useState("");
    // const navigate = useNavigate();
    // const [values, setValues] = useState({
    //   cname: '',
    //   cimage: '',
    //   tname: '',
    //   timage: '',
    //   date: '',
    //   time: '',
    //   ticket: '',
    // });
    // const [errors, setErrors] = useState({});
    // const [concertData, setConcertData] = useState([]); // New state to store the retrieved data
  
    // const handleSubmit = (e) => {
    //   e.preventDefault();
  
    //   fetch("http://localhost:8081/ShowAndBook", {
    //     method: "POST",
    //     headers: {
    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify({
    //       tid: values.tid,
    //       username: values.username,
    //       ticket: values.ticket,
    //       password: values.password,
    //     //   data: values.date,
    //     //   time: values.time,
    //     //   ticket: values.ticket
    //     }),
    //   })
    //     .then((res) => res.json())
    //     .then((data) => {
    //       if (data.message) {
    //         // setRegisterStatus(data.message);
    //         navigate('/Booked');
    //       } else {
    //         // setRegisterStatus("Concert created");
    //         console.log(data);
    //         navigate('/Booked');
    //       }
    //     })
    //     .catch((error) => {
    //       console.error("Error:", error);
    //     });
    // };
  
    // const handleInput = (event) => {
    //   setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));
    // };
    

    const singleSearch = () => {
      // Filter the data based on the entered ID
      const filteredData = concertData.filter((student) => student.id === parseInt(searchedId));
      
      setConcertData2(filteredData);
      setCollapse(false)
    };

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await fetch('http://localhost:8081/ShowAndBookIdDown');
          const data = await response.json();
          
        } catch (error) {
          console.error('Error:', error);
          
        }
      };
      fetchData();
    }, []);
  


  return (
    <>
    <div className=' text-center'>
        {/* <div className="btn-group" role="group" aria-label="Basic example">
  <button type="button" className="btn btn-success" > <Link className="nav-link " aria-current="page" to="/ShowAndBook">Student Information</Link></button> */}
  {/* <button type='button' className='btn btn-danger'> */}
            {/* <Link className='nav-link' aria-current='page' to='/Delete'>
              Delete Ticket
            </Link> */}
            {/* </button> */}
{/* </div> */}

<div>
  
</div>

<h5 className='colorw'>All Books List</h5>
<div className='text-center table-box center2 display'>
<div className='left'>
          <table className="table2">
            <thead >
              <tr >
                <th >Book ID</th>
                <th>Cover</th>
                <th>Title</th>
                <th>Description</th>
            
                <th>Price</th>
              </tr>
            </thead>
            <tbody className='tbody'>
              {currentItems.map((student, index) => (
                <tr key={index}>
                  <td>{student.id}</td>
                 
                  <td>{student.cover}</td>
                  <td>{student.title}</td>
                  <td>{student.description}</td>
                  
                  <td>{student.price}</td>
                  

                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="pagination">
          {Array.from({ length: Math.ceil(concertData.length / itemsPerPage) }, (_, i) => i + 1).map((page) => (
            <button  key={page} onClick={() => paginate(page)} className={`btn ${currentPage === page ? 'btn-success' : 'btn-warning'}`}>
              {page}
            </button>
          ))}
        </div>


 


        <div>
          <input type="number" className='border'  onChange={(e) => setSearchedId(e.target.value)} placeholder="Enter Student ID" />
          <button type="button" className='button border' onClick={singleSearch}>Search</button>
        </div>
</div>


    </div>
    {collapse?<></>:

    <div className='text-center center2'>
          <table className="table2">
            <thead>
              <tr>
              <th >Book ID</th>
                <th>Author ID</th>
                <th>Cover</th>
                <th>Title</th>
                <th>Description</th>
                <th>Genre</th>
                <th>Publish Date</th>
                <th>Price</th>
                <th>#Tags</th>
              </tr>
            </thead>
            <tbody className='tbody'>
              {currentItems2.map((student, index) => (
                <tr key={index}>
                  <td>{student.id}</td>
                  <td>{student.author_id}</td>
                  <td>{student.cover}</td>
                  <td>{student.title}</td>
                  <td>{student.description}</td>
                  <td>{student.genre}</td>
                  <td>{student.publishDate}</td>
                  <td>{student.price}</td>
                  <td>{student.tags}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="center2">
            <button onClick={setReview(true)}>
              Enter Your Review
            </button>
            {review?<input type='text' placeholder='Enter your review here'></input>:<></>}

          </div>

        </div>
}
    </>
  )
}
export default ShowAndBooks;
