import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function ShowAndId(loginID) {
  const [studentData, setStudentData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8081/ShowAndId');
        const data = await response.json();
        if (Array.isArray(data)) {
          setStudentData(data);
        } else {
          setStudentData([]);
        }
      } catch (error) {
        console.error('Error:', error);
        setStudentData([]);
      }
    };

    fetchData();
  }, []);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = studentData.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // const navigate = useNavigate();

  //const [RegisterStatus, setRegisterStatus] = useState("");
  const navigate = useNavigate();
  const [values] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
  });
  // const [errors, setErrors] = useState({});
  // const [concertData, setConcertData] = useState([]); // New state to store the retrieved data

  const handleSubmit = (e) => {
    
    e.preventDefault();

    fetch("http://localhost:8081/Edit2", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        cname: values.id,

       
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.message) {
          // a=values.id;
    // console.log(a);
        //  setRegisterStatus(data.message);
          navigate('/Edit2');
        } else {
          // a=values.id;
    // console.log(a);
        //  setRegisterStatus("Concert created");
          console.log(data);
          navigate('/Edit2');
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  // const handleInput = (event) => {
    

  //   setValues((prev) => ({ ...prev, [event.target.name]: event.target.value }));

  // };
  return (
    <>
      <div className='text-center center2'>
        {/* ... Your existing navigation buttons */}
        <h5 className='colorw'>Students</h5>

        <div>
        <table className="table2">
    <thead>
      <tr>
      <th >Book ID</th>
                <th>Your ID</th>
                <th>Cover</th>
                <th>Title</th>
                <th>description</th>
                <th>genre</th>
                <th>PublishDate</th>
                <th>Price</th>
                <th>Tags</th>
                <th>Status</th>
       
      </tr>
    </thead>
    <tbody className='tbody'>
      {studentData.map((student, index) => (
        <tr key={index}>
          <td>{student.id}</td>
                  <td>{loginID.loginID}</td>
                  <td>{student.cover}</td>
                  <td>{student.title}</td>
                  <td>{student.description}</td>
                  <td>{student.genre}</td>
                  <td>{student.publishDate}</td>
                  <td>{student.price}</td>
                  <td>{student.tags}</td>
                  <td>{student.status}</td>
          
        </tr>
      ))}
    </tbody>
  </table>
        </div>

        <div className="pagination">
          {Array.from({ length: Math.ceil(studentData.length / itemsPerPage) }, (_, i) => i + 1).map((page) => (
            <button key={page} onClick={() => paginate(page)} className={`btn ${currentPage === page ? 'btn-success' : 'btn-warning'}`}>
              {page}
            </button>
          ))}
        </div>

        <h3 className='colorw'>Edit Concert</h3>
        <div className='text-center'>
          <div>
            <button type="submit" onClick={handleSubmit} className="btn btn-success">
              Edit
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default ShowAndId;
